package importantJavaPrograms;

public class Factorial {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		 int num = 27, i = 1;
	        long factorial = 1;
	        while(i <= num)
	        {
	            factorial *= i;
	            i++;
	        }
	        System.out.println(factorial);

	}

}


